var client = require('twilio')('AC3a483933f3f4d019b62283677b485449', 'bbb177963447069b0c8f742febf04194');

exports.sendSms = function(usernumber) {
  usernumber = '+1' + usernumber;
  client.messages.create({
    to: usernumber,
    from: '+17322534528',
    body: "This is the ship that made the Kessel Run in fourteen parsecs?",
  }, function(err, message) {
    if(err){
      console.log(err);
    } else {
      console.log(message.sid);
    }
  });
}

exports.sendCall = function(usernumber) {
  usernumber = '+1' + usernumber;
  client.calls.create({
    url: 'https://handler.twilio.com/twiml/EHe19c34f6a70f078ededa9f44cb851c4c',
    to: usernumber,
    from: "+17322534528"
  }, function(err, call) {
    process.stdout.write(call.sid);
  });
}
